local EnemyRoleSkill = BaseClass("EnemyRoleSkill", UIBaseContainer)
local base = UIBaseContainer

local function GetCrystalSpriteNameByCostType(type)
    if type == 1 then
        return "ui_dtex_Quality_032"
    elseif type == 2 then
        return "ui_dtex_Quality_033"
    elseif type == 3 then
        return "ui_dtex_Quality_034"
    else
        return "ui_dtex_Quality_032"
    end
end

local function GetCrystalSpriteNumByNum(num)
    return "ui_dtex_Quality_0"..math.floor(num+22)
end

local function OpenSkillWindow(self)
    self.view.skill_tip:OnRefresh(self.skill_paras,self.skill_icon.transform.position,1)
end

local function CloseSkillWindow(self)
    self.view.skill_tip:OnShowOrHide(false)
end

local function OnCreate(self)
    base.OnCreate(self)
    self.skill_empty = UIUtil.FindTrans(self.transform,"Skill_Empty").gameObject
    self.skill_frame = self:AddComponent(UIImage,"Skill_ActiveSkill",AtlasConfig.DynamicTex)
    self.skill_icon = self:AddComponent(UIImage,"Skill_Icon",AtlasConfig.DynamicTex)
    self.passive_game = UIUtil.FindTrans(self.transform,"Skill_PassiveSkill").gameObject
    self.crystal_game = UIUtil.FindTrans(self.transform,"Skill_Crystal").gameObject
    self.crystal_icon = self:AddComponent(UIImage,"Skill_Crystal/Skill_Cost_Icon",AtlasConfig.DynamicTex)
    self.crystal_num = self:AddComponent(UIText,"Skill_Crystal/Skill_Cost_Num")
    self.skill_icon_tri = self:AddComponent(UIEventTrigger,"Skill_Icon")
    self.skill_icon_tri:SetOnLongPress(Bind(self,OpenSkillWindow),Bind(self,CloseSkillWindow),true)
end

-- 组件被复用时回调该函数，执行组件的刷新
local function OnRefresh(self,data,costType,m)
    if data ~= nil then
        self.skill_icon.gameObject:SetActive(true)
        self.skill_icon:SetSpriteName(data.icon)
        if data.num > 0 then
            self.crystal_icon:SetSpriteName(GetCrystalSpriteNameByCostType(costType))
            self.crystal_num:SetText(math.floor(data.num))
            self.crystal_game:SetActive(true)
        else
            self.crystal_game:SetActive(false)
        end
        self.skill_empty:SetActive(false)
        if data.skillType == 1 then
            self.passive_game:SetActive(false)
            self.skill_frame.gameObject:SetActive(true)
            self.skill_frame:SetSpriteName("ui_t_Battle_048")
        elseif data.skillType == 2 then
            self.passive_game:SetActive(true)
            self.skill_frame.gameObject:SetActive(false)
        elseif data.skillType == 3 then
            self.passive_game:SetActive(false)
            self.skill_frame.gameObject:SetActive(true)
            self.skill_frame:SetSpriteName("ui_t_Battle_045")
        end
        self.skill_paras = {cost = data.num,name = data.name,id = data.id,level = data.level,costType = costType}
    else
        self.skill_icon.gameObject:SetActive(false)
        self.crystal_game:SetActive(false)
        self.skill_empty:SetActive(true)
        self.passive_game:SetActive(false)
        self.skill_frame.gameObject:SetActive(false)
        if self.view.last_index == 1 and m == 3 then
            self.skill_frame:SetSpriteName("ui_t_Battle_045")
        else
            self.skill_frame:SetSpriteName("ui_t_Battle_048")
        end
    end
end


EnemyRoleSkill.OnRefresh = OnRefresh
EnemyRoleSkill.OnCreate = OnCreate
return EnemyRoleSkill