local UICardGLDingWei=BaseClass("UICardGLDingWei", UIBaseContainer);
local base = UIBaseContainer;

local keyname_path = "skillbg/base/keyname";
local desciable_path = "skillbg/next/next_Scroll/next_text";

local StaticCardData = DataUtil.GetData("card")
local StaticCardTagData = DataUtil.GetData("card_tag")

local function OnCreate(self,_holderSelf)
    base.OnCreate(self);
    self.parent=_holderSelf
    --定位关键词
    self.keynameText=self:AddComponent(UIText,keyname_path);
    self.desciableText=self:AddComponent(UIText,desciable_path);

    self:OnRefresh()

end



--刷新
local function OnRefresh(self)
    local cardData = StaticCardData[self.parent.cardId]
    local str = ""
    local num = #cardData.Tag
    for i = 1, num do
        str = str.."[".. StaticCardTagData[cardData.Tag[i]].Name .."]"
    end
    self.keynameText:SetText(str);
    self.desciableText:SetText(cardData.GuidDesc);
end


local function OnDestroy(self)
    base.OnDestroy(self)
end
