end
local function OnDestroy(self)
    --主界面DesTory 触发

end
UICardGLDingWei.OnDestroy= OnDestroy
UICardGLDingWei.OnDisable=OnDisable
UICardGLDingWei.OnCreate=OnCreate;
UICardGLDingWei.OnRefresh=OnRefresh;
UICardGLDingWei.OnDestroy=OnDestroy;
