local UICardGLWljWrap = BaseClass("UICardGLWljWrap", UIBaseContainer)
local base = UIBaseContainer
local skillitem=require "UI.UICardList.Component.card_skill.UICardSkillWrap"
local skill_data=DataUtil.GetData("skill")
local function OnCreate(self,holder)
    base.OnCreate(self)
    self.holder=holder
    self.skillinfo =skillitem.New(self,"SkillIcon")
    self.skillinfo:OnCreate(self)
    self.skillname=self:AddComponent(UIText, "_name")
    self.skillkeyname=self:AddComponent(UIText, "_keyname")
    self.des_Text = self:AddComponent(UIText, "des_text");
end
local function OnRefresh(self,id,card_id)
    self.data=skill_data[id]
    self.skillinfo:OnRefresh(id,0,card_id)
    self.skillname:SetText(self.data.Name)
    self.des_Text:SetText(self.data.SkillSketchDes)
    local keyStr = "";
    for i = 1, #self.data.FeatureWords do
        keyStr = keyStr.."["..self.data.FeatureWords[i].."]"
    end
    self.skillkeyname:SetText(keyStr)
end

UICardGLWljWrap.OnRefresh = OnRefresh
UICardGLWljWrap.OnCreate = OnCreate
return UICardGLWljWrap