local UICardNationBasProItem = BaseClass("UICardNationBasProItem", UIWrapComponent)
local base = UIWrapComponent

-- 创建
local function OnCreate(self)
    base.OnCreate(self)
    -- 组件初始化
    self.bg = self:AddComponent(UIImage, "")
    self.property1Obj = UIUtil.FindTrans(self.transform, "property1").gameObject
    self.basicText1 = self:AddComponent(UIText, "property1/basicText1")
    self.basicValue1 = self:AddComponent(UIText, "property1/basicValue1")
    self.property2Obj = UIUtil.FindTrans(self.transform, "property2").gameObject
    self.basicText2 = self:AddComponent(UIText, "property2/basicText2")
    self.basicValue2 = self:AddComponent(UIText, "property2/basicValue2")
end

local function OnRefresh(self, real_index, check)
    local itemData = self.view.model.curSelectCardNationData.basic[real_index + 1]
    if itemData == nil then
        return
    end

    self.bg:SetEnabled(real_index % 2 == 0)
    self.basicText1:SetText(itemData.property1.name)
    self.basicValue1:SetText(itemData.property1.value)

    if itemData.property2 ~= nil then
        self.basicText2:SetText(itemData.property2.name)
        self.basicValue2:SetText(itemData.property2.value)
        self.property2Obj:SetActive(true)
    else
        self.property2Obj:SetActive(false)
    end
end

UICardNationBasProItem.OnCreate = OnCreate
UICardNationBasProItem.OnRefresh = OnRefresh

return UICardNationBasProItem