local UICardJLRSkill = BaseClass("UICardJLRSkill", UIBaseContainer)
local UICardSkillWrap= require("UI.UICardList.Component.card_skill.UICardJLRSkillWrap")
local base = UIBaseContainer
local function OnCreate(self,holder)
    base.OnCreate(self)
    self.holder=holder
    self.GridlView=self:AddComponent(UIBaseComponent, "Scroll View/GameObject")
    self.skillGroup={}
    for i = 1, 3 do
        self.skillGroup[i]=UICardSkillWrap.New(self,"Scroll View/GameObject/SkillItem"..i)
        self.skillGroup[i]:OnCreate(self)
    end
end
local function OnRefresh(self,data)
    self.data=self.holder.holder.model.skillList
    if self.card_id~=self.holder.holder.model.select_card_id then
        self.GridlView.rectTransform.anchoredPosition=Vector3.New(-10,0,0)
    end
    self.card_id=self.holder.holder.model.select_card_id
    for i = 1, #self.data do
        self.skillGroup[i].gameObject:SetActive(true)
        self.skillGroup[i]:OnRefresh(self.data[i])
    end
    for i= #self.data+1, 3 do
        self.skillGroup[i].gameObject:SetActive(false)
    end
end
local function OnDisable(self)
    self.card_id=nil

end

UICardJLRSkill.OnRefresh = OnRefresh
UICardJLRSkill.OnCreate = OnCreate
UICardJLRSkill.OnDisable=OnDisable
return UICardJLRSkill