local UILvPointItem = BaseClass("UILvPointItem", UIBaseContainer)
local base = UIBaseContainer


local function OnCreate(self)
    base.OnCreate(self)
    self.lightObj = UIUtil.FindTrans(self.transform,"lvPointLight")
    self.img=self:AddComponent(UIImage, "lvPointLight")
    self.camAnim=UIUtil.FindComponent(self.transform,typeof(CS.UnityEngine.Animator),"lvPointLight")
end

--刷新
local function Refresh(self,_data,_lv,_parentData)
    if _data ~= nil then
        self.parentData = _parentData
        if self.parentData.weaponData.unsealTimes + 1 >= _lv then
            self.lightObj.gameObject:SetActive(true)
            self.img:SetColor32(255,255,255,255)
            if self.parentData.weaponData.unsealTimes + 1 == _lv then
                self.camAnim.enabled = true
            else
                self.camAnim.enabled = false
            end
        else
            self.lightObj.gameObject:SetActive(false)
        end
    end
end


UILvPointItem.OnCreate=OnCreate
UILvPointItem.Refresh=Refresh
return UILvPointItem