local UIBoxWrapItem=BaseClass("UIBoxWrapItem",UIWrapComponent)
local base = UIWrapComponent


local function OnCreate(self)
    base.OnCreate(self)
    --组建初始化
    self.icon_img=self:AddComponent(UIImage,"iconImg",AtlasConfig.DynamicTex)
    self.frame_img = self:AddComponent(UIImage,"frameImg",AtlasConfig.DynamicTex)
    self.name = self:AddComponent(UIText,"nameText")
    self.count = self:AddComponent(UIText,"countText")
    self.info_iconPress=self:AddComponent(UIEventTrigger,"frameImg")
    self.info_iconPress:SetOnLongPress(function()
        UIManager:GetInstance():OpenWindow(UIWindowNames.UIItemTips,self.single_data.id)
    end,(function()
        UIManager:GetInstance():CloseWindow(UIWindowNames.UIItemTips)
    end),true)

end

-- 组件被复用时回调该函数，执行组件的刷新
local function OnRefresh(self,real_index)
    self.single_data=self.view.box_data.rewardList[real_index+1]
    if self.single_data == nil then
        return
    end
    local itemXlsxData= DataUtil.GetDataByIdAndType(self.single_data.id);
    self.icon_img:SetSpriteName(itemXlsxData.icon)
    if itemXlsxData.type==20 or itemXlsxData.type==21 then
        self.frame_img:SetSpriteName(SpriteDefine:GetCircleFrameByType(itemXlsxData.quality));
    else
        self.frame_img:SetSpriteName(SpriteDefine:GetItemQualityFrameByType(itemXlsxData.quality));
    end

    self.name:SetText(itemXlsxData.name)
    self.count:SetText(math.floor(self.single_data.num))
end

UIBoxWrapItem.OnCreate = OnCreate
UIBoxWrapItem.OnRefresh=OnRefresh

return UIBoxWrapItem