local AchievementBtnItem = BaseClass("AchievementBtnItem", UIBaseContainer)
local base = UIBaseContainer


local function OnCreate(self)
    base.OnCreate(self)
    self.select_btn = self:AddComponent(UIButton,"btnImg")
    self.select_btn:SetOnClick(function ()
        LJAudioManger:GetInstance():PlayVoice("UI_CommonClick")
        self.view.OnClickItemAchievementShow(self.view,self,self.bodyData)
    end)
    self.nameText = self:AddComponent(UIText,"btnImg/arrorImg/btn_text")
end

--刷新
local function Refresh(self,bodyData)
    self.bodyData=bodyData
    if bodyData == nil then
        return
    end
    self.nameText:SetText(bodyData.name)
end
AchievementBtnItem.OnCreate=OnCreate
AchievementBtnItem.Refresh=Refresh
return AchievementBtnItem