local AchievementItemResourceBar = BaseClass("AchievementItemResourceBar", UIBaseContainer)
local base = UIBaseContainer


local function OnCreate(self)
    base.OnCreate(self)
    self.sliderText=self:AddComponent(UIText,"footprint_scrollbar/footprint_score_UIText")
    self.nameText = self:AddComponent(UIText,"footprint_scrollbar/footprint_UIText")
    self.slider = self:AddComponent(UISlider,"footprint_scrollbar")
end

--刷新
local function Refresh(self,bodyData)
    self.bodyData=bodyData
    if bodyData == nil then
        return
    end
    self.sliderText:SetText(bodyData.nowPoint.."/"..bodyData.totalPoint)
    self.nameText:SetText(bodyData.name)
    self.slider:SetValue(bodyData.nowPoint/bodyData.totalPoint)
end
AchievementItemResourceBar.OnCreate=OnCreate
AchievementItemResourceBar.Refresh=Refresh
return AchievementItemResourceBar