local PassTopBtnItem = BaseClass("PassTopBtnItem", UIBaseContainer)
local base = UIBaseContainer


local function OnCreate(self)
    base.OnCreate(self)
    self.sliderText=self:AddComponent(UIText,"time_UIText")
    self.selfBtn=self:AddComponent(UIButton,"time_UIText")
    self.selfBtn:SetOnClick(function ()
        self.view.TopBtnOnClick(self.view, self.transform,self.bodyData)
    end)

end

--刷新
local function Refresh(self,bodyData)
    self.bodyData=bodyData
    if bodyData == nil then
        return
    end
    self.sliderText:SetText(bodyData.ResetTime)
end
PassTopBtnItem.OnCreate=OnCreate
PassTopBtnItem.Refresh=Refresh
return PassTopBtnItem