local UIDailyAwardWarpItem=BaseClass("UIDailyAwardWarpItem",UIWrapComponent)
local base = UIWrapComponent


local function OnCreate(self)
    base.OnCreate(self)
    --组建初始化
    self.icon_img=self:AddComponent(UIImage,"awardIcom",AtlasConfig.DynamicTex)
    self.frame_img = self:AddComponent(UIImage,"",AtlasConfig.DynamicTex)
    --self.score = self:AddComponent(UIText,"scoreText")
end

-- 组件被复用时回调该函数，执行组件的刷新
local function Refresh(self,bodyData)
    self.bodyData=bodyData
    if bodyData == nil then
        return
    end
    if bodyData.type == 20 or bodyData.type == 21 then
        self.frame_img:SetSpriteName(SpriteDefine:GetCircleFrameByType (bodyData.quality))
    else
        self.frame_img:SetSpriteName(SpriteDefine:GetItemQualityFrameByType (bodyData.quality))
    end
    self.icon_img:SetSpriteName(bodyData.icon)
end

UIDailyAwardWarpItem.OnCreate = OnCreate
UIDailyAwardWarpItem.Refresh=Refresh

return UIDailyAwardWarpItem