local ArtifactEffectItem=BaseClass("ArtifactEffectItem",UIWrapComponent)
local base = UIWrapComponent



local function OnCreate(self)
    base.OnCreate(self)
    --组建初始化
    self.name_text = self:AddComponent(UIText, "Text")
    self.img = self:AddComponent(UIImage,"bg")
end

-- 组件被复用时回调该函数，执行组件的刷新
local function OnRefresh(self,real_index)
    local data=self.view.level_Info_List[real_index+1]
    if data==nil then
        return
    end
    self.name_text:SetText(data.name)
    self.img.gameObject:SetActive(real_index %2 == 0)
end

ArtifactEffectItem.OnCreate = OnCreate
ArtifactEffectItem.OnRefresh=OnRefresh

return ArtifactEffectItem